'                    p_directory => p_plugin.file_prefix || ''themes/'' ||',
'                                   l_color || ''/'');',
'',
'  --',
'  -- Pace JS',
'  apex_javascript.add_library(p_name           => ''pace.min'',',
'                              p_directory      => p_plugin.file_prefix,',
'                              p_version        => NULL,',
'                              p_skip_extension => FALSE);',
